template.content:
import request from '@/utils/request'
import qs from 'qs'


export function page(params) {
  return request({
    url: '/${urlPrefix}/?' + qs.stringify(params, { indices: false }),
    method: 'get'
  })
}

export function save(data) {
  return request({
    url: '/${urlPrefix}',
    method: 'post',
    data
  })
}

export function del(ids) {
  return request({
    url: '/${urlPrefix}',
    method: 'delete',
    data: ids
  })
}


export function get(id) {
  return request({
    url: '/${urlPrefix}/' + id,
    method: 'get'
  })
}

export default { page, del, save, get }
