INSERT INTO `his_config_info` VALUES (10, 73, 'albedo-gateway-dev.yml', 'DEFAULT_GROUP', '', 'spring:\n  cloud:\n    gateway:\n      locator:\n        enabled: true\n      routes:\n        # 认证中心\n        - id: albedo-auth\n          uri: lb://albedo-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - ValidateCodeGatewayFilter\n            # 前端密码解密\n            - PasswordDecoderFilter\n        #系统管理 模块\n        - id: albedo-sys\n          uri: lb://albedo-sys\n          predicates:\n            - Path=/sys/**\n          filters:\n            # 限流配置\n            - name: RequestRateLimiter\n              args:\n                key-resolver: \'#{@remoteAddrKeyResolver}\'\n                redis-rate-limiter.replenishRate: 100\n                redis-rate-limiter.burstCapacity: 200\n        # 代码生成模块\n        - id: albedo-gen\n          uri: lb://albedo-gen\n          predicates:\n            - Path=/gen/**\n        # 任务调度模块\n        - id: albedo-quartz\n          uri: lb://albedo-quartz\n          predicates:\n            - Path=/quartz/**\n\n\ngateway:\n  encode-key: \'somewhere-albedo\'\n  ignore-clients:\n    - test\n\nswagger:\n  ignore-providers:\n    - albedo-auth\n    - albedo-gen\n    - swagger\n', '2bb2075093b8bea074c747f2b22afcbc', '2010-05-05 00:00:00', '2021-12-15 13:56:46', 'nacos', '127.0.0.1', 'U', '');
