INSERT INTO `his_config_info` VALUES (1, 81, 'application-dev.yml', 'DEFAULT_GROUP', '', '# 加解密根密码\njasypt:\n  encryptor:\n    password: albedo #根密码\n\n\ndubbo:\n  scan:\n    base-packages: com.albedo.java.*\n  protocols:\n    dubbo:\n      name: dubbo\n      port: -1\n  registry:\n    #   The Spring Cloud Dubbo\'s registry extension\n    ##  the default value of dubbo-provider-services is \"*\", that means to subscribe all providers,\n    ##  thus it\'s optimized if subscriber specifies the required providers.\n    address: spring-cloud://localhost\n  cloud:\n    # The subscribed services in consumer side\n    subscribed-services: albedo-sys\n\n# Spring 相关\nspring:\n  redis:\n    host: albedo-redis\n  datasource:\n    type: com.zaxxer.hikari.HikariDataSource\n    driver-class-name: ${application.mysql.driverClassName}\n    username: ${application.mysql.username}\n    password: ${application.mysql.password}\n    url: ${application.mysql.url}\n  mvc:\n    pathmatch:\n      matching-strategy: ant_path_matcher\n  cloud:\n    sentinel:\n      filter:\n        enabled: false\n      eager: true\n      transport:\n        dashboard: albedo-sentinel:8858\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n  endpoint:\n    health:\n      show-details: always\n  logfile:\n    enabled: true\n#配置日志地址\nlogging:\n  config: classpath:logback-spring.xml\n  file:\n    name: logs/${spring.application.name}/${spring.application.name}-info.log\n\n# feign 配置\nfeign:\n  hystrix:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 10000\n        readTimeout: 10000\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n      useGzipDecoder: true\n\n#请求处理的超时时间\nribbon:\n  ReadTimeout: 10000\n  ConnectTimeout: 10000\n\n# mybaits-plus配置\nmybatis-plus:\n  mapper-locations: classpath*:/mapper/*/*Mapper.xml\n  global-config:\n   